mapper.select:
SELECT pd.program_date_no programDateNo, p.program_no programNo, p.program_name programName, pd.program_date programDate, pd.createdate, pd.updatedate FROM program_date pd INNER JOIN program p ON pd.program_no = p.program_no
WHERE pd.program_date_no = #{programDateNo}
ORDER BY program_date desc

mapper.insert:
INSERT INTO program_date (
	program_no,
	program_date
) VALUES (
	#{programNo},
	#{programDate}
)
