mapper.select:
SELECT se.sports_equipment_no sportsEquipmentNo, se.item_name itemName, se.item_price itemPrice, se.equipment_active equipmentActive, (SELECT sei.sports_equipment_img_file_name FROM sports_equipment_img sei WHERE sei.sports_equipment_no = se.sports_equipment_no LIMIT 1 ) sportsEquipmentImgFileName FROM sports_equipment se
${item.key} ${item.operator} #{item.value}
AND item_name LIKE CONCAT('%',#{query},'%')
AND equipment_active = #{active}
${item.key} ${item.value}
ORDER BY se.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(sports_equipment_no) FROM sports_equipment
AND item_name LIKE CONCAT('%',#{query},'%')
AND equipment_active = #{active}

SELECT b.branch_name branchName, se.item_name itemName, se.item_price itemPrice, seo.quantity quantity, seo.order_no orderNo, seo.total_price totalPrice, seo.order_status orderStatus, seo.createdate createdate, seo.updatedate updatedate, (SELECT sei.sports_equipment_img_file_name FROM sports_equipment_img sei WHERE sei.sports_equipment_no = se.sports_equipment_no LIMIT 1) sportsEquipmentImgFileName FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON se.sports_equipment_no = seo.sports_equipment_no
AND b.branch_name LIKE CONCAT('%',#{searchBranch},'%')
AND se.item_name LIKE CONCAT('%',#{query},'%')
AND seo.createdate >= #{beginDate}
AND seo.createdate <= #{endDate}
ORDER BY seo.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(seo.order_no) FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON se.sports_equipment_no = seo.sports_equipment_no
AND b.branch_name LIKE CONCAT('%',#{searchBranch},'%')
AND se.item_name LIKE CONCAT('%',#{query},'%')
AND seo.createdate >= #{beginDate}
AND seo.createdate <= #{endDate}

SELECT b.branch_name branchName, se.item_name itemName, se.item_price itemPrice, seo.quantity quantity, seo.order_no orderNo, seo.total_price totalPrice, seo.order_status orderStatus, seo.createdate createdate, seo.updatedate updatedate, (SELECT sei.sports_equipment_img_file_name FROM sports_equipment_img sei WHERE sei.sports_equipment_no = se.sports_equipment_no LIMIT 1) sportsEquipmentImgFileName FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON se.sports_equipment_no = seo.sports_equipment_no
AND b.branch_no = #{loginBranchNo}
AND se.item_name LIKE CONCAT('%',#{query},'%')
AND seo.createdate > #{beginDate}
AND seo.createdate < #{endDate}
ORDER BY seo.createdate DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(seo.order_no) FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON se.sports_equipment_no = seo.sports_equipment_no
AND b.branch_no = #{loginBranchNo}
AND se.item_name LIKE CONCAT('%',#{query},'%')
AND seo.createdate > #{beginDate}
AND seo.createdate < #{endDate}

mapper.delete:
DELETE FROM sports_equipment_order
WHERE order_no = #{orderNo}
AND order_status= '대기'

mapper.select:
SELECT DISTINCT b.branch_name branchName, se.item_name itemName, se.item_Price itemPrice, se.sports_equipment_no sportsEquipmentNo, ( SELECT sei.sports_equipment_img_file_name FROM sports_equipment_img sei WHERE sei.sports_equipment_no = se.sports_equipment_no ORDER BY sei.sports_equipment_img_file_name LIMIT 1 ) sportsEquipmentImgFileName, SUM(seo.quantity) AS totalQuantity, SUM(CASE WHEN seo.quantity > 0 THEN seo.quantity ELSE 0 END) AS inventoryQuantity, SUM(CASE WHEN seo.quantity < 0 THEN seo.quantity ELSE 0 END) AS discartdQuantity FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON seo.sports_equipment_no = se.sports_equipment_no INNER JOIN sports_equipment_img sei ON se.sports_equipment_no = sei.sports_equipment_no
WHERE seo.order_status = '승인'
AND b.branch_no = #{loginBranchNo}
AND se.item_name LIKE CONCAT('%',#{query},'%')
GROUP BY b.branch_name, se.item_name, se.sports_equipment_no, sei.sports_equipment_no, sei.sports_equipment_img_file_name ORDER BY se.item_name LIMIT #{beginRow}, #{rowPerPage}

SELECT (SELECT COUNT(*) FROM ( SELECT b.branch_name AS branchName FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON seo.sports_equipment_no = se.sports_equipment_no WHERE seo.order_status = '승인'
AND b.branch_no = #{loginBranchNo}
AND se.item_name LIKE CONCAT('%',#{query},'%')
GROUP BY b.branch_name, se.item_name ) AS subquery) AS totalRowCount FROM branch b INNER JOIN sports_equipment_order seo ON b.branch_no = seo.branch_no INNER JOIN sports_equipment se ON seo.sports_equipment_no = se.sports_equipment_no WHERE seo.order_status = '승인' LIMIT 1;
